// src/components/TeamMember.js
import React from 'react';

const FriendMember = ({ name, role, image, styleType }) => {
  return (
    <div className={`flex flex-col items-center p-4 ${styleType === 'Style1' ? 'space-y-2' : 'space-y-4'}`}>
      <img
        src={image}
        alt={`${name}`}
        className={`rounded-full ${styleType === 'Style1' ? 'w-24 h-24' : 'w-20 h-20'}`}
      />
      <div className="text-center">
        <h3 className="font-bold">{name}</h3>
        <p className="text-gray-600">{role}</p>
      </div>
      <div className="flex space-x-2">
        <a href="#"><i className="fab fa-facebook"></i></a>
        <a href="#"><i className="fab fa-twitter"></i></a>
        <a href="#"><i className="fab fa-instagram"></i></a>
      </div>
    </div>
  );
};

export default FriendMember;
